function processJob(job){
    node.log("Start new order: " + job.jobId);

    processedJobs[job.jobId] = {
        "id"        : job.jobId,
        "startedOn" : Date.now(),
        "status"    : "pending",
        "audit"     : [
            {
                type : "info",
                on   : Date.now(),
                msg  : "Order has been prepared."
            }    
        ],
    };

    //check for errors while reading job
    //this is optional and only used, if job has property error and errorMsg
    if(job.hasOwnProperty("error")){
        if(job.error === true){
            processedJobs[job.jobId].status = "error";
            if(job.hasOwnProperty("errorMsg")){
                for (let errorMsg of job.errorMsg)
                    processedJobs[job.jobId].audit.push(errorMsg); 
            }
        }

        //success: error property exists, but no error occured
        else{
           node.send({ job: job }) 
        }
    }

    //default: always send
    else{
        node.send({ job: job })
    }
}

