 **/
var WebsocketManager = function() {

    // the reconnect timeinterval in ms
    this.reconnectInterval = 500;

    // the websocket url
    this.websocketUrl = "wss://" + env.get("SPO_URL") + "/api/websocket/notifications";

    // websocket headers
    this.headers = {
        'Accept': 'application/json',
        'X-Tenant': env.get("SPO_TENANT") ,
        'Authorization': 'Basic ' + Buffer.from(env.get("SPO_USER") + ':' + env.get("SPO_PASSWORD")).toString('base64')
    }

    var tagsToReplace = {
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;'
    };

    var replaceTag = function(tag) {
        return tagsToReplace[tag] || tag;
    }

    var safe_tags_replace = function(str) {
        return str.replace(/[&<>]/g, replaceTag);
    }

    /**
     * Method for connecting the SPO WebSocket.
     */ 
    this.connect = function() {
        node.log('Connecting to SPO Websocket ' + this.websocketUrl +'...');


        var manager = this;



        var headerLines = [];
        for(var name in this.headers) {
            headerLines.push(name + ': ' + this.headers[name]);
        }
        var headersString = Buffer.from(headerLines.join("\r\n")).toString('base64').replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '.');

        this.websocket = new WebSocket(this.websocketUrl, ['Headers', 'Headers-' + headersString]);

        /**
         * Function is called when connection has been established.
         */ 
        this.websocket.onopen = function(openEvent) {
            node.log('SPO Websocket connection has been established successfully.');
            flow.set('websocket',true);
        };

        /**
         * Function is called when connection has been closed.
         */ 
        this.websocket.onclose = function(closeEvent) {
            node.error('Connection lost/refused, retry in ' + (manager.reconnectInterval / 1000) + ' seconds');
            flow.set('websocket',false);
            setTimeout(function() {
                manager.connect();
            }, manager.reconnectInterval);
        };

        /**
         * Function is called in case of an error.
         */ 
        this.websocket.onerror = function(errorEvent) {
            node.error('Error occured: ' + errorEvent);
        };

        /**
         * Function is called when a message has been received.
         */ 
        this.websocket.onmessage = function(messageEvent) {
            node.send({
                notification: JSON.parse(messageEvent.data)
            });
        };

    };

    /**
     * Method for closing the SPO WebSocket connection.
     */
    this.close = function() {

        var manager = this;

        this.websocket.onclose = function(closeEvent) {
            node.log('WebSocket connection has been closed.');
            flow.set('websocket',false);
        };

        this.websocket.close();

    };

};


