function updateServiceStatus(service){
    return new Promise(function(resolve, reject){
        if(service.type === "pibService"){
            if(service.name === "websocket"){
                service.status = flow.get("websocket") ? "online" : "offline";
                resolve(service);
            }else{
                console.log("ERROR at websocket");
                resolve(service);
            }
        }

        else if(service.type === "pibWebService"){

            //if no location for the service is found
            if(!service.location){
                service.status = "offline";
                node.error("Deployment error: Got no environment variable for location of service " + service.name);
                resolve(service);
            }

            var url = urljoin(service.location,'version');
            axios({
                method : "get",
                url : url
            })
            .then(response => {
                var data = response.data;

                //Imposition service (so far)
                if (data.hasOwnProperty('appName') && data['appName'] === service.name){
                    service.status = 'online';
                    service.version = data['appVersion'];
                    service.description = "";
                    resolve(service);
                }

                //other pibWebServices
                else if(data.hasOwnProperty('name') && data['name'] === service.name){
                    service.status = 'online';
                    service.version = data['versionNumber'];
                    service.description = data['description'];
                    resolve(service);
                }

                else{
                    node.warn("Wrong service name: Expected: '" + service.name + "', got: '" + (data['appName'] || data['name']) + "'.  Unable to connect to " + service.title + " at '" + service.location + "'");
                    node.log(data);
                    service.status = "offline";
                    resolve(service);
                }

                resolve(service);
            })
            .catch(err => {
                node.warn("Error: Unable to connect to " + service.title + " at '" + url + "'");
                service.status = "offline";
                resolve(service);
            });
        }

        else{
            resolve(service);
        }
    })
}

function checkService(serviceKeys, index){
    index = ((index + 1) <= (serviceKeys.length - 1) ? index + 1 : 0); //resets after keys length is reached

    var serviceKey = serviceKeys[index];
    var service = cacheServices[serviceKey];

    updateServiceStatus(service)
    .then(service => {
        cacheServices[serviceKey] = service;

        //recall itself
        setTimeout(function(){
            checkService(serviceKeys, index);
        },  wait);
    })
}

